            اخطارات غير معالجة
          </h2>
          <h2 className="text-2xl font-semibold  text-bg-gray-800 font-montserrat text-center mb-8">
           {statisticNonTraiter}
          </h2>
        </div>
      </div>
    </div>
  );
};

export default CardView;
